    public function printURLPDF(Request $request)
    {
        $pdf = PDF::loadFile($request->input('url'));
        return $pdf->download($request->input('name') . '.pdf');
    }
    public function seeURLPDF(Request $request)
    {
        $pdf = PDF::loadFile($request->input('url'));
